mapper.select:
select t.*,t2.name as site_name,t3.name as org_name from t_si_role t left outer join t_si_site t2 on t.site_id = t2.id left outer join t_si_organization t3 on t.org_id = t3.id
t.org_id = #{filterMap.org_id}
t.org_id in
#{org.id}
and t.name like concat('%',#{filterMap.name},'%') and t.site_id = #{filterMap.site_id} and t.id != 1
#{key} #{val}
, builddatetime desc
order by builddatetime desc

        select r.*,s.name as site_name,o.name as org_name   
        from t_si_role r left outer join t_si_site s 
        on r.site_id = s.id 
        left outer join t_si_organization o 
        on r.org_id = o.id 
        
        	r.org_id = #{orgId} 
        	and ( r.buildoper = #{loginname} 
        	
        		
        			 or r.id in 
		        	
			            #{role.id}
			        
        		
        		
        	)
			 and r.name like concat('%',#{search_name},'%')
			 and r.site_id = #{site_id}
			and r.id != 1

mapper.update:
update t_si_role
name=#{name}, url=#{url}, modifyoper=#{modifyoper}, modifydatetime=#{modifydatetime},
